mapper.update:
UPDATE tb_answerrecord AS ar
    JOIN tb_question AS q ON ar.questionid = q.id
    JOIN tb_paperquestion AS pq ON q.id = pq.questionid
    SET ar.score = pq.score
WHERE ar.answer = q.answer AND ar.questiontype = #{questionType}
    AND ar.paperid = pq.paperid AND ar.sid=#{studentId};

UPDATE  tb_answerrecord
SET score = #{score}
WHERE  questionid=#{questionId} AND sid=#{studentId} AND paperid=#{paperId} ;
